package array;

//1.1
public class UniqueSymbolsChecker {

    public boolean isUnique(String str) {
        if (str.length() > 256) { //ASCII
            return false;
        }

        boolean[] unique = new boolean[256];
        for (int i = 0; i < str.length(); i++) {
            int val = str.charAt(i);
            if (unique[val]) {
                return false;
            }
            unique[val] = true;
        }

        return true;
    }

    public static void main(String[] args) {
        UniqueSymbolsChecker us = new UniqueSymbolsChecker();
        System.out.println(us.isUnique(""));
        System.out.println(us.isUnique("aa"));
        System.out.println(us.isUnique("ab"));
        System.out.println(us.isUnique(""));
    }
}
